import axios from "axios";
import React, { useState } from "react";
import { useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import { AuthContext } from "../context/authContext";

const Login = () => {
  // gérer les entrées utilisateur
  const [inputs, setInputs] = useState({
    username: "",
    password: "",
  });
  // État local pour gérer les erreurs
  const [err, setError] = useState(null);

  // Utilisation du useNavigate pour la navigation
  const navigate = useNavigate();

  // Utilisation du contexte d'authentification
  const { login } = useContext(AuthContext);

  // Gestionnaire de changement pour les champs de saisie
  const handleChange = (e) => {
    setInputs((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  // Gestionnaire de soumission du formulaire
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Appel à la fonction de connexion du contexte d'authentification
      await login(inputs);
      // Redirection vers la page d'accueil après une connexion réussie
      navigate("/");
    } catch (err) {
      setError(err.response.data);
    }
  };

  // Rendu du composant
  return (
    <div className="auth">
      <h1>Identification</h1>
      <form>
        {/* Champs de saisie pour le nom d'utilisateur et le mot de passe */}
        <input
          required
          type="text"
          placeholder="username"
          name="username"
          onChange={handleChange}
        />
        <input
          required
          type="password"
          placeholder="password"
          name="password"
          onChange={handleChange}
        />
        <button onClick={handleSubmit}>S'identifier</button>
        {/* Affichage des erreurs, le cas échéant */}
        {err && <p>{err}</p>}
        <span>
          Vous n'avez pas de compte? <Link to="/register">S'inscrire</Link>
        </span>
      </form>
    </div>
  );
};

export default Login;
